var irrigationData = {};
if (msg.topic) {
    var topicItems = msg.topic.split("/");
    if (topicItems.length==3) {
        irrigationData.sysId = topicItems[1];
        switch(topicItems[2]) {
            case 'data' :
            var content = JSON.parse(msg.content);
            var measures = content.measures;
            if (measures) {
            irrigationData.temperature=measures.temperature;
            irrigationData.humidity=measures.humidity;
            irrigationData.sm=measures.sm;
            irrigationData.valve=measures.valve;
            irrigationData.timestamp=measures.timestamp;
            if (!irrigationData.timestamp) {
                irrigationData.timestamp = new Date().toISOString();
            }
            newMsg.payload = irrigationData;
            newMsg.ontology = 'sres_irrigation_data';
            }
            break;            
        }
    }
    return newMsg;
}

